import { useContext, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import AuthContext from "../../store/auth-context";

import classes from "./AuthForm.module.css";

const ResetPasswordForm = () => {
  const authCtx = useContext(AuthContext);
  const history = useHistory();
  const passwordInputRef = useRef();
  const passwordConfirmationInputRef = useRef();


  const [isLoading, setIsLoading] = useState(false);

  const submitHandler = (event) => {
    event.preventDefault();
    const enteredPassword = passwordInputRef.current.value;
    const enteredPasswordConfirmation = passwordConfirmationInputRef.current.value;

    setIsLoading(true);
    const url = "https://boiler-stage.ibtikar.sa/api/v1/users/password/reset";
    console.log(authCtx.forgotPasswordToken);

    fetch(url, {
      method: "POST",
      body: JSON.stringify({
        token: authCtx.forgotPasswordToken,
        email: authCtx.forgotPasswordEmail,
        password: enteredPassword,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        setIsLoading(false);
        if (res.ok) {
          return res.json();
        } else {
          return res.json().then((data) => {
            console.log(data);
            let errorMessage = "Authentication failed!";
            throw new Error(errorMessage);
          });
        }
      })
      .then((data) => {
        console.log('pushed');
        history.replace("/login");
      })
      .catch((err) => {
        alert(err.message);
      });
  };

  return (
    <section className={classes.auth}>
      <h1>Reset password</h1>
      <form onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            required
            ref={passwordInputRef}
          />
        </div>
        <div className={classes.control}>
          <label htmlFor="password_confirmation">Password confirmation</label>
          <input
            type="password"
            id="password_confirmation"
            required
            ref={passwordConfirmationInputRef}
          />
        </div>
        <div className={classes.actions}>
          {!isLoading && <button>Submit</button>}
          {isLoading && <p> Laoding... </p>}
        </div>
      </form>
    </section>
  );
};

export default ResetPasswordForm;
