import { useContext, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import AuthContext from "../../store/auth-context";

import classes from "./AuthForm.module.css";

const ForgotPasswordForm = () => {
  const authCtx = useContext(AuthContext);
  const history = useHistory();
  const tokenInputRef = useRef();

  const [isLoading, setIsLoading] = useState(false);

  const submitHandler = (event) => {
    event.preventDefault();
    const enteredToken = codeInputRef.current.value;
    console.log(authCtx.forgotPasswordEmail);
    setIsLoading(true);
    const url =
      "https://boiler-stage.ibtikar.sa/api/v1/users/password/validate-token";

    fetch(url, {
      method: "POST",
      body: JSON.stringify({
        token: enteredToken,
        email: authCtx.forgotPasswordEmail,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        setIsLoading(false);
        if (res.ok) {
          return res.json();
        } else {
          return res.json().then((response) => {
            console.log(response);
            let errorMessage = "Authentication failed!";
            throw new Error(errorMessage);
          });
        }
      })
      .then((response) => {
        console.log(response);
        authCtx.login(response.data.access_token);
        history.replace("/reset");
      })
      .catch((err) => {
        alert(err.message);
      });
  };

  return (
    <section className={classes.auth}>
      <h1>Verify code</h1>
      <form onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor="token">Code</label>
          <input type="text" id="token" required ref={tokenInputRef} />
        </div>
        <div className={classes.actions}>
          {!isLoading && <button>Submit</button>}
          {isLoading && <p> Laoding... </p>}
        </div>
      </form>
    </section>
  );
};

export default ForgotPasswordForm;
