import { useContext, useRef, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import AuthContext from "../../store/auth-context";
import useBasicInput from "../hooks/use-basic-input";

import classes from "./AuthForm.module.css";

const LoginForm = () => {
  const authCtx = useContext(AuthContext);
  const history = useHistory();
  const passwordInputRef = useRef();

  const validateEmail = (email) => {
    const re =
      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  const {
    value: enteredEmail,
    enteredValueIsValid: emailIsValid,
    valueHasError: emailHasError,
    valueChangehandler: emailChangehandler,
    valueBlurHandler: emailBlurHandler,
    reset: emailReset,
  } = useBasicInput(validateEmail);

  const {
    value: enteredPassword,
    enteredValueIsValid: passwordIsValid,
    valueHasError: passwordHasError,
    valueChangehandler: passwordChangehandler,
    valueBlurHandler: passwordBlurHandler,
    reset: passwordReset,
  } = useBasicInput((value) => value.length !== "");

  const [isLoading, setIsLoading] = useState(false);

  const submitHandler = (event) => {
    event.preventDefault();
    const enteredPassword = passwordInputRef.current.value;

    setIsLoading(true);
    const url = "https://boiler-stage.ibtikar.sa/api/v1/users/login";

    fetch(url, {
      method: "POST",
      body: JSON.stringify({
        email: enteredEmail,
        password: enteredPassword,
        client_id: 2,
        client_secret: "fhMZQxfVREJrII50IeN4ThIZCerdOFjxiRGu7Lc0",
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        setIsLoading(false);
        if (res.ok) {
          return res.json();
        } else {
          return res.json().then((response) => {
            console.log(response);
            let errorMessage = "Authentication failed!";
            throw new Error(errorMessage);
          });
        }
      })
      .then((response) => {
        console.log(`Access token = ${response.data.access_token}`);
        authCtx.login(response.data.access_token);
        history.push("/");
      })
      .catch((err) => {
        alert(err.message);
      });
  };

  return (
    <section className={classes.auth}>
      <h1>Login</h1>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            !emailIsValid ? classes.invalid : ""
          }`}
        >
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            required
            onChange={emailChangehandler}
            onBlur={emailBlurHandler}
            value={enteredEmail}
          />
          {emailHasError && (
            <p className={classes.error}>Email format is invalid</p>
          )}
        </div>
        <div className={classes.control}>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            required
            ref={passwordInputRef}
          />
        </div>
        <div className={classes.actions}>
          {!isLoading && <button>Login</button>}
          {isLoading && <p> Laoding... </p>}
          <Link to="/forgot-password">Forgot password?</Link>
        </div>
      </form>
    </section>
  );
};

export default LoginForm;
