import { useContext, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import AuthContext from "../../store/auth-context";
import "react-phone-number-input/style.css";
import PhoneInput from "react-phone-number-input";
import classes from "./AuthForm.module.css";
import useBasicInput from "../hooks/use-basic-input";

const RegisterForm = () => {
  const authCtx = useContext(AuthContext);
  const history = useHistory();
  const passwordConfirmationInputRef = useRef();
  const mobileNumberInputRef = useRef();

  const validateEmail = (email) => {
    const re =
      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  const {
    value: enteredEmail,
    enteredValueIsValid: emailIsValid,
    valueHasError: emailHasError,
    valueChangehandler: emailChangehandler,
    valueBlurHandler: emailBlurHandler,
    reset: emailReset,
  } = useBasicInput(validateEmail);

  const {
    value: enteredName,
    enteredValueIsValid: nameIsValid,
    valueHasError: nameHasError,
    valueChangehandler: nameChangehandler,
    valueBlurHandler: nameBlurHandler,
    reset: nameReset,
  } = useBasicInput((value) => value.trim() !== "");

  const {
    value: enteredPassword,
    enteredValueIsValid: passwordIsValid,
    valueHasError: passwordHasError,
    valueChangehandler: passwordChangehandler,
    valueBlurHandler: passwordBlurHandler,
    reset: passwordReset,
  } = useBasicInput((value) => !(value.length < 8));

  const [isLoading, setIsLoading] = useState(false);

  const submitHandler = (event) => {
    event.preventDefault();
    const enteredPasswordConfirmation =
      passwordConfirmationInputRef.current.value;
    const enteredMobileNumber = mobileNumberInputRef.current.value;

    setIsLoading(true);
    const url = "https://boiler-stage.ibtikar.sa/api/v1/users";

    fetch(url, {
      method: "POST",
      body: JSON.stringify({
        email: enteredEmail,
        password: enteredPassword,
        name: enteredName,
        password_confirmation: enteredPasswordConfirmation,
        mobile_number: enteredMobileNumber,
        returnSecureToken: true,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        setIsLoading(false);
        if (res.ok) {
          return res.json();
        } else {
          return res.json().then((data) => {
            console.log(data);
            let errorMessage = "Authentication failed!";
            throw new Error(errorMessage);
          });
        }
      })
      .then((data) => {
        console.log("pushed");
        history.push("/login");
      })
      .catch((err) => {
        alert(err.message);
      });
  };

  return (
    <section className={classes.auth}>
      <h1>Register</h1>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            nameHasError ? classes.invalid : ""
          }`}
        >
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            required
            value={enteredName}
            onChange={nameChangehandler}
            onBlur={nameBlurHandler}
          />
          {nameHasError && <p className={classes.error}>Name is required</p>}
        </div>
        <div
          className={`${classes.control} ${
            emailHasError ? classes.invalid : ""
          }`}
        >
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            required
            value={enteredEmail}
            onChange={emailChangehandler}
            onBlur={emailBlurHandler}
          />
          {emailHasError && (
            <p className={classes.error}>Email format is invalid</p>
          )}
        </div>
        <div className={classes.control}>
          <label htmlFor="mobile_number">Mobile number</label>
          <input
            type="text"
            id="mobile_number"
            required
            ref={mobileNumberInputRef}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordHasError ? classes.invalid : ""
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            required
            onChange={passwordChangehandler}
            onBlur={passwordBlurHandler}
            value={enteredPassword}
          />
          {passwordHasError && (
            <p className={classes.error}>Invalid password</p>
          )}
        </div>
        <div className={classes.control}>
          <label htmlFor="password_confirmation">Password confirmation</label>
          <input
            type="password"
            id="password_confirmation"
            required
            ref={passwordConfirmationInputRef}
          />
        </div>
        <div className={classes.control}>
          <PhoneInput international defaultCountry="EG" />
        </div>
        <div className={classes.actions}>
          {!isLoading && <button>Create Account</button>}
          {isLoading && <p> Laoding... </p>}
        </div>
      </form>
    </section>
  );
};

export default RegisterForm;
